var cssify = require('cssify')
var domify = require('domify')

cssify.byUrl('//storage.googleapis.com/code.getmdl.io/1.0.2/material.indigo-pink.min.css')

document.head.appendChild(
  domify(
    '<script src="https://storage.googleapis.com/code.getmdl.io/1.0.2/material.min.js"></script>'
  )
)

document.head.appendChild(
  domify(
    '<link rel="stylesheet" href="https://fonts.googleapis.com/icon?family=Material+Icons">'
  )
)

module.exports = {
  row: 'div.mdl-cell.mdl-cell--12-col',
  cell: function (col) {
    return 'div.mdl-cell.mdl-cell--' + col + '-col'
  },
  container: 'div.mdl-layout.mdl-layout__content.mdl-grid'
}
