var cssify = require('cssify')
cssify.byUrl('//cdnjs.cloudflare.com/ajax/libs/normalize/3.0.3/normalize.min.css')
cssify.byUrl('//cdnjs.cloudflare.com/ajax/libs/skeleton/2.0.4/skeleton.min.css')

var ntw = require('number-to-words')

module.exports = {
  row: 'div.row',
  cell: function (col) {
    return ['div', ntw.toWords(col), 'columns'].join('.')
  },
  container: 'div.container'
}
